int main()
{
	int game_start = 0;
	int hinverted = 0;
	int toggle = 0;
	int jump_toggle = 0;
	int jumped = 0;
	int32_t cactus_count = 0;
	int cactus_visible[2] = {0};
	int cactus_speed = 3;
	int min_cactus_interval = 30;
	int loop_count = min_cactus_interval;
	int jump_loop_count = 0;
	int score = 0;
	int highest_score = 0;
	uint16_t x = 35;
	uint16_t y = 80;
	uint16_t cactus_x[] = {104,104};
	uint16_t cactus_y = 80;
	uint16_t oldx = x;
	uint16_t oldy = y;
	initClock();
	initSysTick();
	setupIO();
	initSound();
	srand(time(0));

	// menu page
	printTextX2("DINO", 6, 20, RGBToWord(0xff,0xff,0), 0);
	printText("Press down button", 6, 40, RGBToWord(0xff,0xff,0), 0);
	putImage(33,65,48,64,menu_dino,0,0);
	
	while(1)
	{
		// start the game
		if ((GPIOA->IDR & (1 << 11)) == 0) // down pressed
		{
			// set up the game environment
			fillRectangle(0,0,128,160,32996);
			fillRectangle(0,112,128,64,23117);
			putImage(x,y,24,32,dinosaur1,0,0);
			game_start = 1;
			redOff();
			greenOn();
			printText("Score:",47,3,RGBToWord(0xff,0xff,0),32996);
			printNumber(score,92,3,RGBToWord(0xff,0xff,0),32996);
		}

		else // control the lights
		{
			greenOff();
			redOn();
		}

		while (game_start)
		{
			// generate cactus pseudo-randomly
			if (loop_count > ((rand() % 20) + min_cactus_interval))
			{
				if (cactus_x[cactus_count] == 104)
				{
					cactus_x[cactus_count]--;
					putImage(cactus_x[cactus_count],cactus_y,24,32,cactus1,0,0);
					cactus_visible[cactus_count] = 1;
					cactus_count++;
					if (cactus_count > 1)
						cactus_count = 0;
				}
				loop_count = 0;
			}
			loop_count++;
			
			// score
			score++;
			printNumber(score,92,3,RGBToWord(0xff,0xff,0),32996);

			// cactus motion and check if they are visible
			if ((!((isInside(cactus_x[0],cactus_y,24,32,x,y) || isInside(cactus_x[0],cactus_y,24,32,x+24,y) || isInside(cactus_x[0],cactus_y,24,32,x,y+32) || isInside(cactus_x[0],cactus_y,24,32,x+24,y+32)))) && (cactus_visible[0] == 1))
			{
				if (cactus_x[0] > 0 && cactus_x[0] < 104)
				{
					fillRectangle(cactus_x[0],80,24,32,32996);
					cactus_x[0] -= cactus_speed;
					putImage(cactus_x[0],cactus_y,24,32,cactus1,0,0);
				}
				if (cactus_x[0] < cactus_speed)
				{
					fillRectangle(cactus_x[0],80,24,32,32996);
					cactus_x[0] = 104;
					cactus_visible[0] = 0;
				}
			}
			if ((!((isInside(cactus_x[1],cactus_y,24,32,x,y) || isInside(cactus_x[1],cactus_y,24,32,x+24,y) || isInside(cactus_x[1],cactus_y,24,32,x,y+32) || isInside(cactus_x[1],cactus_y,24,32,x+24,y+32)))) && cactus_visible[1] == 1)
			{
				if (cactus_x[1] > 0 && cactus_x[1] < 104)
				{
					fillRectangle(cactus_x[1],80,24,32,32996);
					cactus_x[1] -= cactus_speed;
					putImage(cactus_x[1],cactus_y,24,32,cactus1,1,0);
				}
				if (cactus_x[1] < cactus_speed)
				{
					fillRectangle(cactus_x[1],80,24,32,32996);
					cactus_x[1] = 104;
					cactus_visible[1] = 0;
				}
			}
			
			if ((GPIOA->IDR & (1 << 8)) == 0) // up pressed
			{
				jumped = 1;
				playNote(0);
			}

			// dinosaur walking motion
			if (y == 80 && loop_count % 3 == 0)
			{
				if (toggle)
					putImage(x,y,24,32,dinosaur1,hinverted,0);
				else
					putImage(x,y,24,32,dinosaur2,hinverted,0);
				
				toggle = toggle ^ 1;
			}

			// game over
			if ((isInside(cactus_x[1],cactus_y,24,32,x,y) || isInside(cactus_x[1],cactus_y,24,32,x+24,y) || isInside(cactus_x[1],cactus_y,24,32,x,y+32) || isInside(cactus_x[1],cactus_y,24,32,x+24,y+32)) || (isInside(cactus_x[0],cactus_y,24,32,x,y) || isInside(cactus_x[0],cactus_y,24,32,x+24,y) || isInside(cactus_x[0],cactus_y,24,32,x,y+32) || isInside(cactus_x[0],cactus_y,24,32,x+24,y+32)))
			{
				// play game over music
				playNote(DS4_Eb4);
				delay(150);
				playNote(A4);
				delay(100);
				playNote(GS3_Ab3);
				delay(50);
				playNote(0);

				// display game over
				fillRectangle(0,0,128,20,32996);
				printTextX2("GAME OVER",12,8,RGBToWord(0xff,0xff,0),32996);
				printText("Press down button",6,35,RGBToWord(0xff,0xff,0),32996);

				// save player's highest_score
				if (score > highest_score)
				{
					highest_score = score;
					printText("New Best:",8,50,RGBToWord(0xff,0xff,0),32996);
				}
				else
					printText("High Score:",8,50,RGBToWord(0xff,0xff,0),32996);
				printNumber(highest_score,87,50,RGBToWord(0xff,0xff,0),32996);

				// reset game variables
				game_start = 0;
				y = 80;
				jumped = 0;
				jump_loop_count = 0;
			}
			
			// dinosaur jumping motion
			if (jumped)
			{
				jump_loop_count++;
				
				// dinosaur jump motion and play jump music
				if (jump_loop_count <= 4 && jump_loop_count % 2 == 0)
				{
					y -= 20;
					jump_toggle ^= 1;
					if (jump_loop_count == 2)
						playNote(D4);
					else if (jump_loop_count == 4)
						playNote(FS4_Gb4);
				}

				// stop jump music
				else if (jump_loop_count == 6)
					playNote(0);

				// dinosaur land motion
				else if (jump_loop_count >= 24 && jump_loop_count <= 27 && jump_loop_count % 3 ==0)
				{
					y += 20;
					jump_toggle ^= 1;
				}

				// reset jumping motion variables
				else if (jump_loop_count > 27)
				{
					jump_loop_count = 0;
					jumped = 0;
				}

				// display the dinosaur jumping
				if (oldy != y)
				{
					fillRectangle(oldx,oldy,24,32,32996);
					oldy = y;
					if (jump_toggle)
						putImage(x,y,24,32,dinosaur_jump1,hinverted,0);
					else
						putImage(x,y,24,32,dinosaur_jump2,hinverted,0);
				}
			}
						
			delay(50);
		}
		// reset game variables
		score = 0;
		cactus_x[1] = 104;
		cactus_x[0] = 104;

	}
		
	return 0;
}





void initSysTick(void)
{
	SysTick->LOAD = 48000;
	SysTick->CTRL = 7;
	SysTick->VAL = 10;
	__asm(" cpsie i "); // enable interrupts
}
void SysTick_Handler(void)
{
	milliseconds++;
}
void initClock(void)
{
// This is potentially a dangerous function as it could
// result in a system with an invalid clock signal - result: a stuck system
        // Set the PLL up
        // First ensure PLL is disabled
        RCC->CR &= ~(1u<<24);
        while( (RCC->CR & (1 <<25))); // wait for PLL ready to be cleared
        
// Warning here: if system clock is greater than 24MHz then wait-state(s) need to be
// inserted into Flash memory interface
				
        FLASH->ACR |= (1 << 0);
        FLASH->ACR &=~((1u << 2) | (1u<<1));
        // Turn on FLASH prefetch buffer
        FLASH->ACR |= (1 << 4);
        // set PLL multiplier to 12 (yielding 48MHz)
        RCC->CFGR &= ~((1u<<21) | (1u<<20) | (1u<<19) | (1u<<18));
        RCC->CFGR |= ((1<<21) | (1<<19) ); 

        // Need to limit ADC clock to below 14MHz so will change ADC prescaler to 4
        RCC->CFGR |= (1<<14);

        // and turn the PLL back on again
        RCC->CR |= (1<<24);        
        // set PLL as system clock source 
        RCC->CFGR |= (1<<1);
}
void delay(volatile uint32_t dly)
{
	uint32_t end_time = dly + milliseconds;
	while(milliseconds != end_time)
		__asm(" wfi "); // sleep
}

void enablePullUp(GPIO_TypeDef *Port, uint32_t BitNumber)
{
	Port->PUPDR = Port->PUPDR &~(3u << BitNumber*2); // clear pull-up resistor bits
	Port->PUPDR = Port->PUPDR | (1u << BitNumber*2); // set pull-up bit
}
void pinMode(GPIO_TypeDef *Port, uint32_t BitNumber, uint32_t Mode)
{
	/*
	*/
	uint32_t mode_value = Port->MODER;
	Mode = Mode << (2 * BitNumber);
	mode_value = mode_value & ~(3u << (BitNumber * 2));
	mode_value = mode_value | Mode;
	Port->MODER = mode_value;
}
int isInside(uint16_t x1, uint16_t y1, uint16_t w, uint16_t h, uint16_t px, uint16_t py)
{
	// checks to see if point px,py is within the rectange defined by x,y,w,h
	uint16_t x2,y2;
	x2 = x1+w;
	y2 = y1+h;
	int rvalue = 0;
	if ( (px >= x1) && (px <= x2))
	{
		// ok, x constraint met
		if ( (py >= y1) && (py <= y2))
			rvalue = 1;
	}
	return rvalue;
}

void setupIO()
{
	RCC->AHBENR |= (1 << 18) + (1 << 17) + (1 << 22); // enable Ports A and B
	display_begin();
	pinMode(GPIOB,4,0);
	pinMode(GPIOB,5,0);
	pinMode(GPIOA,8,0);
	pinMode(GPIOA,11,0);
	pinMode(GPIOB,3,1);
	pinMode(GPIOF,0,1);
	enablePullUp(GPIOB,4);
	enablePullUp(GPIOB,5);
	enablePullUp(GPIOA,11);
	enablePullUp(GPIOA,8);
}

void redOn()
{
	GPIOF->ODR |= (1 << 0);
}
void redOff()
{
	GPIOF->ODR &= ~(1 << 0);
}
void greenOn()
{
	GPIOB->ODR |= (1 << 3);
}
void greenOff()
{
	GPIOB->ODR &= ~(1 << 3);
}

